(function(){if('function'!==typeof jQuery){return}
var loggedOutUsersAdvice='<p class="tribe-support-advice"> <strong>Existing customer? To receive the fastest support possible, '+'please <a href="https://support.theeventscalendar.com/login">login</a> before posting.</strong> '+'In some cases, you may need to register first of all: be sure to do so using the same email address '+'you used when purchasing your license key!</p>'+'<p class="tribe-support-advice">To learn more about accessing support or if you experience any problems, '+'please refer to '+'<a href="https://support.theeventscalendar.com/812859-Accessing-Support">this article</a>.</p>'
var loginFormAdvice='<p class="tribe-support-advice">âš  Please note that your login credentials for '+'<a href="https://theeventscalendar.com">theeventscalendar.com</a> will not work here! Our Help Desk runs '+'on a different platform and a separate account is needed.</p>'+'<p class="tribe-support-advice">We do automatically link accounts where possible, though. So, for most '+'customers, if you first login to <a href="https://theeventscalendar.com">theeventscalendar.com</a> '+'you should be automatically logged in here, too. If you find that doesn&#146;t work for you, please try '+'a password reset in the first instance or else register for a new account.</p>'+'<p class="tribe-support-advice">Be sure to use the same email address as you used when purchasing '+'your license key!</p>'
jQuery(function($){var $jumbotronHeading=$('.jumbotron h1')
var $presalesForm=$('#presales-form')
var isLoggedIn=$('#menu-item-user').length===1
var currentUrl=getCurrentUrl()
var isHomePage='support.theeventscalendar.com'===currentUrl
var isLoginPage=currentUrl.match(/login/)!==null
var isMyTicketsPage=currentUrl.match(/my_tickets/)!==null
var isSubmitTicketPage=currentUrl.match(/submit_ticket/)!==null
function getCurrentUrl(){var url=document.location.toString()
var protocolSeparator=url.match(/:\/\//)
if('/'===url.slice(-1)){url=url.slice(0,-1)}
if(protocolSeparator!==null){url=url.slice(protocolSeparator.index+3)}
return url}
function addAccountHelperText(){if(isLoggedIn||$jumbotronHeading.length!==1){return}
if(isLoginPage||isSubmitTicketPage||isMyTicketsPage){$jumbotronHeading.after(loginFormAdvice)}
else{$jumbotronHeading.after(loggedOutUsersAdvice)}}
function optionallyRemovePresalesForm(){if(!isLoggedIn||$presalesForm.length!==1){return}
$presalesForm.hide()}
addAccountHelperText()
optionallyRemovePresalesForm()})})()